import "./MainPage.style.css"
import AddPet from "../../components/AddPet/AddPet.component"
import ListPets from "../../components/ListPets/ListPets.component";
import axios from "axios";
import { useEffect, useState } from "react";
import HTTPClient from "../../utils/HTTPClient";

const MainPage = (props) => {

    const [pets, setPets] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [name, setName] = useState("");
    const [type, setType] = useState("");
    const [description, setDescription] = useState("");
    const [skill1, setSkill1] = useState("");
    const [skill2, setSkill2] = useState("");
    const [skill3, setSkill3] = useState("");

    const getPets = () => {
        axios.get('http://localhost:8000/api/pets')
            .then((response) => {
                setPets(response.data.pets)
                setLoaded(response.true)
                console.log(response.data)
            })
            .catch((error) => {
                console.log(error)
            })
    }

    const createPet = (data) => {
        axios.post('http://localhost:8000/api/pets/new',{
            name,
            type,
            description,
            skill1,
            skill2,
            skill3
        } )
            .then((response) => {
                getPets();
            })
            .catch((error) => {
                console.log(error)
            })
        
    }

    useEffect(()=>{
        getPets();
    },[])


    return <div className="content">
        <h1 className="text-center">Pagina de mascotas</h1>
        <div className="bg-blue">   
            <AddPet createPet={createPet} />
        </div>
        <div>
            <ListPets pets={pets} />
        </div>
    </div>
}

export default MainPage;
