import "./MainPage.style.css"
import AddPet from "../../components/AddPet/AddPet.component"
import ListPets from "../../components/ListPets/ListPets.component";
import axios from "axios";
import { useEffect, useState } from "react";
import HTTPClient from "../../utils/HTTPClient";

const MainPage = (props) => {

    const [pets, setPets] = useState([]);
    const [loaded, setLoaded] = useState(false);

    const getPets = () => {
        axios.get('http://localhost:8000/api/pets')
            .then((response) => {
                setPets(response.data.pets)
                setLoaded(response.true)
                console.log(response.data)
            })
            .catch((error) => {
                console.log(error)
            })
    }

    const createPet = (data) => {
        let client = new HTTPClient();
        client.createPet(data)
            .then((response) => {
                getPets();
            })
            .catch((error) => {
                console.log(error)
            })
        
    }

    useEffect(()=>{
        getPets();
    },[])


    return <div className="content">
        <h1 className="text-center">Pagina de mascotas</h1>
        <div className="bg-blue">   
           { loaded && <AddPet createPet={createPet} />}
        </div>
        <div>
            <ListPets pets={pets} />
        </div>
    </div>
}

export default MainPage;
